import chalk from "chalk";
import { ModelError } from "../model/error.js";
export class BridgePluginAPI {
    runningConnector;
    constructor(ConfiguredNetlifyConnector) {
        this.runningConnector = ConfiguredNetlifyConnector;
    }
    Error(message) {
        return new ModelError(message);
    }
    log(message, { symbolColor, symbol } = {}) {
        if (!symbolColor || !(symbolColor in chalk))
            symbolColor = `green`;
        if (!symbol)
            symbol = `->`;
        if (symbol.length === 1)
            symbol = `${symbol} `;
        const label = chalk.grey(this.runningConnector.slug);
        console.log(`${label} \n ${chalk[symbolColor](symbol)} ${message}\n`);
    }
}
